import React, { Component } from "react";
import ScatterJS from "scatterjs-core";
import ScatterEOS from "scatterjs-plugin-eosjs";
import Eos from "eosjs";

class Order extends Component {
  constructor(props) {
    super(props);

    this.state = {
      Orders: [],
      OrderSells: [],
      useraccount: ""
    };
  }

  componentDidMount() {
    ScatterJS.plugins(new ScatterEOS());

    const network = {
      blockchain: "eos",
      protocol: "http",
      host: "13.57.210.230",
      eosVersion: "bf28f8bb",
      port: 8888,
      chainId:
        "cf057bbfb72640471fd910bcb67639c22df9f92470936cddc1ade0e2f2e7dc4f",
      debug: false,
      verbose: false,
      latency: 200
    };

    ScatterJS.scatter.connect("My-Apps").then(connected => {
      if (!connected) return false;
      var scatter = ScatterJS.scatter;
      this.setState({ useraccount: scatter.identity.name });
      console.log(this.state.useraccount);
    });
    // console.logthis.state.sc
    //alert(scatter.identity.publicKey);

    var API =
      "http://api.byzanti.ne:8902/orders?symbol=IQ&side=BUY&size=100&api_key=FQK0SYR-W4H4NP2-HXZ2PKH-3J8797N";
    var OrderSell =
      "http://api.byzanti.ne:8902/orders?symbol=IQ&side=SELL&size=100&api_key=FQK0SYR-W4H4NP2-HXZ2PKH-3J8797N";
    fetch(API)
      .then(response => response.json())
      .then(data => {
        this.setState({ Orders: data });
      });
    fetch(OrderSell)
      .then(response => response.json())
      .then(data => {
        this.setState({ OrderSells: data });
      });
  }

  render() {
    const { Orders } = this.state;
    const { OrderSells } = this.state;

    return (
      <div className="orderD">
        <div className="container clearfix">
          <h3>Open Orders</h3>
          <table className="openOrder">
            <thead>
              <tr>
                <th>Coin</th>
                <th>Type </th>
                <th>Entrusted Time</th>
                <th>Price</th>
                <th>Average</th>
                <th>Amount</th>
                <th>Dealt</th>
                <th>Entrusted </th>
                <th>Status </th>
                <th>Account Name</th>
              </tr>
            </thead>
            <tbody>
              {Orders.map(order => {
                if ("poiuytrewqas" == order.useraccount)
                  return (
                    <tr>
                      <td className="plus">{order.assetBuy}</td>
                      <td>{order.type}</td>
                      <td>{order.created}</td>
                      <td>{order.price}</td>
                      <td />
                      <td>{order.amountBuy}</td>
                      <td>{order.created}</td>
                      <td>Yes</td>
                      <td />
                      <td>{order.useraccount}</td>
                    </tr>
                  );
              })}
              {OrderSells.map(sells => {
                if (this.state.useraccount == sells.useraccount)
                  return (
                    <tr>
                      <td className="minus">{sells.assetBuy}</td>
                      <td>{sells.type}</td>
                      <td>{sells.created}</td>
                      <td>{sells.price}</td>
                      <td />
                      <td>{sells.amountBuy}</td>
                      <td>{sells.created}</td>
                      <td>Yes</td>
                      <td />
                      <td>{sells.useraccount}</td>
                    </tr>
                  );
              })}
            </tbody>
          </table>

          <h3>Orders History</h3>
          <table>
            <thead>
              <tr>
                <th>Coin</th>
                <th>Type </th>
                <th>Entrusted Time</th>
                <th>Price</th>
                <th>Average</th>
                <th>Amount</th>
                <th>Dealt</th>
                <th>Total </th>
                <th>Status </th>
                <th>Action</th>
              </tr>
            </thead>
            <tbody />
          </table>
        </div>
      </div>
    );
  }
}

export default Order;
