import React, {Component} from 'react';

class Home_banner extends Component{
    constructor(props) {
        super(props);
    
        this.state = {
          hits: [],
        };
      }

    componentDidMount() {
        var url = new URL(window.location.href);
        var c = url.searchParams.get("opt");
        var API = 'http://api.byzanti.ne:8902/ticker?symbol='+c+'&api_key=FQK0SYR-W4H4NP2-HXZ2PKH-3J8797N';
        fetch(API)
        .then(response => response.json())
        .then(data => {this.setState({ hits: data }); });
    }


    render(){
       
        const { hits } = this.state;
        return(
            <div className="trading_head">
                <div className="container">
                
                {hits.map(hit =>
                    <div className="trading clearfix">
                        <div className="lefts">
                            <span>{hit.currency}</span>
                            <h4>{hit.currency} / <small> EOS</small></h4>
                            <p>Introduction</p>
                        </div>
                        <div className="rights">
                            <ul>
                                <li>
                                    <p>Last Price</p>
                                    <span className="plus">{hit.last}</span> EOS
                                </li>
                                <li>
                                    <p>24H Change</p>
                                    <span className="minus">{hit.change}</span>
                                </li>
                                <li>
                                    <p>24H High</p>
                                    <span>{hit.high} </span> EOS
                                </li>
                                <li>
                                    <p>24H Low</p>
                                    <span>{hit.low} </span> EOS
                                </li>
                                
                                <li>
                                    <p>24H Volume </p>
                                    <span>{hit.volume} </span> {hit.currency}
                                </li>
                            </ul>
                        </div>
                    </div>
                
                )}
                </div>
            </div>
        )
    }
}

export default Home_banner;
